using System;\nusing System.IO;\nusing System.Xml;\nusing System.Xml.Xsl;\n\npublic class HtmlToAspxConverter\n{\n    public void Convert(string inputHtml, string xsltFile, string outputAspx)\n    {\n        XslCompiledTransform transform = new XslCompiledTransform();\n        using (XmlReader reader = XmlReader.Create(inputHtml))\n        {\n            XmlWriterSettings settings = new XmlWriterSettings { Indent = true };\n            using (XmlWriter writer = XmlWriter.Create(outputAspx, settings))\n            {\n                transform.Load(xsltFile);\n                transform.Transform(reader, writer);\n            }\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        var converter = new HtmlToAspxConverter();\n        converter.Convert("input.html", "transform.xsl", "output.aspx");\n    }\n}