using System;\nusing System.Web.Services.Description;\nusing Microsoft.Web.Services3.Security;\n\nclass Program\n{\n    static void Main()\n    {\n        // Example of how to create a policy assertion programmatically\n        Policy policy = new Policy();\n        UsernameOverTransportAssertion usernameAssertion = new UsernameOverTransportAssertion();\n        policy.Assertions.Add(usernameAssertion);\n\n        Console.WriteLine("Policy created with the following assertions:");\n        foreach (var assertion in policy.Assertions)\n        {\n            Console.WriteLine(assertion.GetType().Name);\n        }\n    }\n}