using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass Program {\n    static void Main() {\n        float[] data = { 1.2f, 3.4f, 5.6f, 7.8f, 9.0f, 2.4f, 6.4f, 8.2f };\n        int k = 5; // Number of top values to find\n\n        var topKValuesWithIndices = GetTopKValuesAndIndices(data, k);\n\n        foreach (var item in topKValuesWithIndices) {\n            Console.WriteLine($"Value: {item.Value}, Index: {item.Index}");\n        }\n    }\n\n    static List<(float Value, int Index)> GetTopKValuesAndIndices(float[] data, int k) {\n        // Use a min-heap to track the top k values and their indices\n        var minHeap = new SortedSet<(float Value, int Index)>();\n\n        for (int i = 0; i < data.Length; i++) {\n            if (minHeap.Count < k) {\n                minHeap.Add((data[i], i));\n            } else if (data[i] > minHeap.Min.Value) {\n                minHeap.Remove(minHeap.Min);\n                minHeap.Add((data[i], i));\n            }\n        }\n\n        return minHeap.ToList();\n    }\n}