using System;\nusing System.Web;\n\npublic class ContentHandler : IHttpHandler\n{\n    public bool IsReusable { get { return false; } }\n\n    public void ProcessRequest(HttpContext context)\n    {\n        string url = context.Request.Url.Segments[1];\n        \n        // Example URL: /Content/Articles/1234/how-to-stackoverflow-helps-you-in-your-development\n        if (url.Equals("Content/", StringComparison.OrdinalIgnoreCase))\n        {\n            string type = context.Request.Url.Segments[2]; // e.g., "Articles/"\n            int id;\n            \n            // Check if the ID is a number\n            if (int.TryParse(context.Request.Url.Segments[3], out id))\n            {\n                // Redirect to your content page, passing the ID\n                context.Response.Redirect($"~/viewContent.aspx?Id={id}", true);\n                return;\n            }\n        }\n\n        // Return 404 for unmatched URLs\n        context.Response.StatusCode = 404;\n        context.Response.StatusDescription = "Not Found";\n    }\n}