public override string Text\n{\n    get\n    {\n        return base.Text;\n    }\n    set\n    {               \n        double tempDollarAmount = 0;\n        string tempVal = value.Replace("$", "").Replace(",","");\n        if (double.TryParse(tempVal, out tempDollarAmount))\n        {\n            base.Text = string.Format("{0:C}", tempDollarAmount);\n        }\n        else\n        {                   \n            base.Text = "$0.00";\n        }               \n    }\n}