[ApiController]\n[Route("api/[controller]")]\npublic class OrdersController : ControllerBase\n{\n    private readonly ILogger<OrdersController> _logger;\n    private readonly IOrderService _orderService;\n\n    public OrdersController(ILogger<OrdersController> logger, IOrderService orderService)\n    {\n        _logger = logger;\n        _orderService = orderService;\n    }\n\n    [HttpPost("{id}")]\n    public async Task<IActionResult> UpdateOrder(int id, [FromBody] Order update)\n    {\n        try\n        {\n            // Validate and process the order update\n            await _orderService.ProcessOrderUpdate(id, update);\n            return Ok("Order updated successfully.");\n        }\n        catch (Exception ex)\n        {\n            _logger.LogError(ex, "Failed to update order with ID {OrderId}", id);\n            return StatusCode(500, $"Unable to update order with ID {id} due to server error.");\n        }\n    }\n}