using System;\nusing System.Collections.Generic;\nusing System.IO;\nusing System.Linq;\n\npublic class Program\n{\n    public static void Main()\n    {\n        var items = new List<Item>\n        {\n            new Item { StartDate = "2023-10-05", Id = 1, WorkItemId = 101, AccountNumber = "123456789", FirstName = "John", LastName = "Doe", Address = "123 Elm St", Description = "Description 1" },\n            // Add more items as needed\n        };\n\n        var templatePath = "ReportTemplate.html";\n        var reportHtml = GenerateReportFromTemplate(templatePath, items);\n        \n        Console.WriteLine(reportHtml);\n    }\n\n    public static string GenerateReportFromTemplate(string templatePath, List<Item> items)\n    {\n        var templateContent = File.ReadAllText(templatePath);\n\n        if (items == null || !items.Any())\n        {\n            return templateContent;\n        }\n\n        // Create a place to store the rows\n        var rowTemplates = new List<string>();\n\n        // Read one row of the template\n        var rowTemplateStartIndex = templateContent.IndexOf("<tr>") + 4; // Length of "<tr>"\n        var rowTemplateEndIndex = templateContent.IndexOf("</tr>");\n        var rowPlaceholder = templateContent.Substring(rowTemplateStartIndex, rowTemplateEndIndex - rowTemplateStartIndex);\n        \n        foreach (var item in items)\n        {\n            var rowHtml = rowPlaceholder\n                .Replace("{0, 10}", item.StartDate)\n                .Replace("{1}", item.Id.ToString())\n                .Replace("{2, 8}", item.WorkItemId.ToString())\n                .Replace("{3}", item.AccountNumber)\n                .Replace("{4, -30}", $"{item.FirstName} {item.LastName}")\n                .Replace("{5}", item.Address)\n                .Replace("{6}", item.Description);\n            \n            rowTemplates.Add(rowHtml);\n        }\n\n        var rows = string.Join(Environment.NewLine, rowTemplates);\n\n        // Inject rows into the full template\n        templateContent = templateContent.Replace("<!-- ROWS_PLACEHOLDER -->", rows);\n\n        return templateContent;\n    }\n}\n\npublic class Item\n{\n    public string StartDate { get; set; }\n    public int Id { get; set; }\n    public int WorkItemId { get; set; }\n    public string AccountNumber { get; set; }\n    public string FirstName { get; set; }\n    public string LastName { get; set; }\n    public string Address { get; set; }\n    public string Description { get; set; }\n}