using System;\nusing System.Runtime.InteropServices;\n\n[ComImport, Guid("00012345-0000-0000-0000-000000000011"), InterfaceType(ComInterfaceType.InterfaceIsDual)]\npublic interface Dummy\n{\n    void Foo(ref int x);\n}\n\nclass Test\n{\n    static void Main()\n    {\n        // Manually get the type from CLSID and create instance\n        Guid clsid = new Guid("00012345-0000-0000-0000-000000000011");\n        Type type = Type.GetTypeFromCLSID(clsid);\n        \n        if (type != null)\n        {\n            Dummy dummy = (Dummy)Activator.CreateInstance(type);\n\n            int value = 10;\n            dummy.Foo(ref value);\n            Console.WriteLine("Value after Foo: " + value);\n        }\n        else\n        {\n            Console.WriteLine("Type not found for CLSID.");\n        }\n    }\n}