using System;\nusing System.IO;\nusing System.Windows;\nusing System.Collections.Generic;\nusing Microsoft.Office.Interop.Word;\nusing Excel = Microsoft.Office.Interop.Excel;\nusing PowerPoint = Microsoft.Office.Interop.PowerPoint;\n\nnamespace PrintApp\n{\n    public partial class MainWindow : Window\n    {\n        private List<string> documentsList = new List<string>\n        {\n            @"C:\path\to\mydoc.docx",\n            @"C:\path\to\mysheet.xlsx",\n            @"C:\path\to\mypresentation.pptx",\n            @"C:\path\to\myfile.pdf"\n        };\n\n        public MainWindow()\n        {\n            InitializeComponent();\n            ComboBoxFiles.ItemsSource = documentsList;\n        }\n\n        private void PrintButton_Click(object sender, RoutedEventArgs e)\n        {\n            var selectedFilePath = ComboBoxFiles.SelectedItem as string;\n\n            if (!string.IsNullOrEmpty(selectedFilePath))\n            {\n                switch (Path.GetExtension(selectedFilePath).ToLower())\n                {\n                    case ".pdf":\n                        // Use a library like PdfSharp or iTextSharp to print PDF\n                        break;\n                    case ".docx":\n                        PrintWordDocument(selectedFilePath);\n                        break;\n                    case ".xlsx":\n                        PrintExcelSpreadsheet(selectedFilePath);\n                        break;\n                    case ".pptx":\n                        PrintPowerPointPresentation(selectedFilePath);\n                        break;\n                }\n            }\n        }\n\n        private void PrintWordDocument(string filePath)\n        {\n            var wordApp = new Application();\n            var document = wordApp.Documents.Open(filePath);\n            document.PrintOut();\n            document.Close();\n            wordApp.Quit();\n        }\n\n        private void PrintExcelSpreadsheet(string filePath)\n        {\n            var excelApp = new Excel.Application();\n            var workbook = excelApp.Workbooks.Open(filePath);\n            workbook.PrintOutEx(1, 1); // Adjust parameters as needed\n            excelApp.Quit();\n        }\n\n        private void PrintPowerPointPresentation(string filePath)\n        {\n            var powerpointApp = new PowerPoint.Application();\n            var presentation = powerpointApp.Presentations.Open(filePath);\n            presentation.PrintOptions.Ranges.Add(PowerPoint.PpPrintRangeType.ppPrintAll);\n            presentation.PrintOut();\n            presentation.Close();\n            powerpointApp.Quit();\n        }\n    }\n}