// Example of API method that could be part of an MVC controller or Web Service\npublic class EmployeeController : Controller {\n    private EmployeeBL _employeeBL = new EmployeeBL();\n\n    public IActionResult GetAllEmployees() {\n        var employees = _employeeBL.GetAllEmployees();\n        return Ok(employees);\n    }\n\n    [HttpPost]\n    public IActionResult AddEmployee([FromBody] Employee employee) {\n        try {\n            _employeeBL.AddEmployee(employee);\n            return CreatedAtAction("GetAllEmployees", new { });\n        } catch (Exception ex) {\n            // error handling and logging\n            return StatusCode(500, ex.Message);\n        }\n    }\n}