// Partial classes for adding methods to entity classes\n   partial class Employee {\n       partial void OnValidate(System.ComponentModel.ChangeAction action) {\n           if (this.Name == null || string.IsNullOrEmpty(this.Name.Trim())) {\n               throw new ArgumentException("Name is required.");\n           }\n            // other validations...\n       }\n   }\n\n   public class EmployeeBL {\n       private HRDataContext _dataContext;\n       \n       public EmployeeBL() {\n           _dataContext = new HRDataContext();\n       }\n       \n       public IEnumerable<Employee> GetAllEmployees() {\n           return _dataContext.Employees.ToList();\n       }\n\n       public void AddEmployee(Employee employee) {\n           // business logic before inserting\n           _dataContext.Employees.InsertOnSubmit(employee);\n          try{\n               _dataContext.SubmitChanges(); \n          } catch (Exception ex){\n              //logging and error handling\n          }\n       }\n   }