protected void Page_Load(object sender, EventArgs e)\n{\n    // Example: Your binary data and filename\n    byte[] fileBytes = GetYourBinaryData(); // Replace with your method to get bytes\n    string fileName = "example.pdf"; // Replace with your actual filename\n\n    // Clear the response\n    Response.Clear();\n\n    // Set the content type - adjust this based on the file type you are sending\n    Response.ContentType = GetContentType(fileName);\n\n    // Set the default Content-Disposition header\n    Response.AddHeader("Content-Disposition", $"attachment; filename={fileName}");\n\n    // Write the file bytes to the response output stream\n    Response.BinaryWrite(fileBytes);\n\n    // End the response\n    Response.End();\n}\n\nprivate byte[] GetYourBinaryData()\n{\n    // Replace this with actual logic to get your binary data\n    return new byte[0]; // Placeholder\n}\n\nprivate string GetContentType(string fileName)\n{\n    // Use a dictionary or switch statement for mapping file extensions to MIME types\n    switch (Path.GetExtension(fileName).ToLower())\n    {\n        case ".pdf":\n            return "application/pdf";\n        case ".jpg":\n        case ".jpeg":\n            return "image/jpeg";\n        case ".png":\n            return "image/png";\n        default:\n            return "application/octet-stream"; // Default for unknown types\n    }\n}