using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass ColorNum {\n    public string m_Color { get; set; }\n    public int m_Number { get; set; }\n\n    public ColorNum(string color, int number) {\n        m_Color = color;\n        m_Number = number;\n    }\n}\n\nclass Program {\n    static void Main() {\n        List<ColorNum> ColorNumList = new List<ColorNum>();\n        ColorNumList.Add(new ColorNum("Red", 25));\n        ColorNumList.Add(new ColorNum("Blue", 5));\n        ColorNumList.Add(new ColorNum("Red", 11));\n        ColorNumList.Add(new ColorNum("White", 25));\n\n        var sortedList = ColorNumList\n            .OrderByDescending(c => c.m_Color == "Red")\n            .ThenBy(c => c.m_Number) // Optional: Sort by m_Number if needed\n            .ToList();\n\n        foreach (var item in sortedList) {\n            Console.WriteLine($"{item.m_Color}, {item.m_Number}");\n        }\n    }\n}