using System;\nusing System.Net.Sockets;\nusing System.Text;\n\nclass SocketClient\n{\n    static void Main(string[] args)\n    {\n        try\n        {\n            TcpClient client = new TcpClient("localhost", 8080); // Replace with your server name and port\n            NetworkStream stream = client.GetStream();\n\n            string message = "Your custom message here"; // This must align with the service's expected format\n            byte[] data = Encoding.UTF8.GetBytes(message);\n\n            // Send request to WCF Service\n            stream.Write(data, 0, data.Length);\n            Console.WriteLine("Sent: {0}", message);\n\n            // Get response from WCF service\n            data = new byte[256];\n            int bytes = stream.Read(data, 0, data.Length);\n            string responseData = Encoding.UTF8.GetString(data, 0, bytes);\n            Console.WriteLine("Received: {0}", responseData);\n\n            // Close everything.\n            stream.Close();\n            client.Close();\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine("Exception: {0}", e.Message);\n        }\n    }\n}