using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = "example.txt";\n        string content = "This is a sample text file.";\n\n        // Writing to the file\n        using (StreamWriter writer = new StreamWriter(filePath))\n        {\n            writer.Write(content);\n        }\n\n        Console.WriteLine("File has been written successfully.");\n    }\n}