using System;\nusing System.Text.RegularExpressions;\n\npublic class DNSSanitizer\n{\n    // Validate host label against DNS rules\n    public static bool IsValidHostLabel(string label)\n    {\n        if (label.Length > 63) return false;\n        return Regex.IsMatch(label, @"^[a-zA-Z0-9]([-a-zA-Z0-9]*[a-zA-Z0-9])?$");\n    }\n\n    // Validate full domain name\n    public static bool IsValidDomain(string domain)\n    {\n        return Regex.IsMatch(domain, @"^([a-zA-Z0-9](-?[a-zA-Z0-9]+)*\.)+([a-zA-Z]{2,})$") && domain.Length <= 255;\n    }\n\n    // Validate IPv4 address\n    public static bool IsValidIPv4(string ip)\n    {\n        return Regex.IsMatch(ip, @"^(?:[0-9]{1,3}\.){3}[0-9]{1,3}$");\n    }\n\n    // Validate IPv6 address\n    public static bool IsValidIPv6(string ip)\n    {\n        return Regex.IsMatch(ip, @"^(([0-9a-fA-F]{1,4}):){7}([0-9a-fA-F]{1,4})$");\n    }\n}