// Define the SQL query with parameter placeholders\nstring sql = "SELECT [WallPost], [DatePosted] FROM [WallTable] WHERE [UserId] = @UserId";\n\n// Retrieve the connection string from Web.config\nstring strCon = System.Web.Configuration.WebConfigurationManager.ConnectionStrings["SocialSiteConnectionString"].ConnectionString;\n\nusing (SqlConnection conn = new SqlConnection(strCon))\n{\n    using (SqlCommand cmd = new SqlCommand(sql, conn))\n    {\n        // Add parameters to prevent SQL injection\n        cmd.Parameters.AddWithValue("@UserId", Request["friend"]);\n\n        using (SqlDataAdapter daUserProfile = new SqlDataAdapter(cmd))\n        {\n            dsSocialSite.UserProfileDataTable tbUserProfile = new dsSocialSite.UserProfileDataTable();\n            daUserProfile.Fill(tbUserProfile);\n\n            // Bind the data table to the repeater\n            rpWall2.DataSource = tbUserProfile;\n            rpWall2.DataBind();\n        }\n    }\n}