using (WebClient wc = new WebClient())\n{\n    string xml = wc.DownloadString("http://steve-yegge.blogspot.com/feeds/posts/default");\n    XmlNamespaceManager nsMngr = new XmlNamespaceManager(new NameTable());\n    nsMngr.AddNamespace("atom", "http://www.w3.org/2005/Atom"); // Define the atom namespace prefix\n    nsMngr.AddNamespace("app", "http://purl.org/atom/app#");      // Define the app namespace prefix\n\n    XmlDocument atom = new XmlDocument();\n    atom.LoadXml(xml);\n\n    // Use the defined prefixes in your XPath query\n    XmlNode node = atom.SelectSingleNode("/atom:feed/atom:entry/atom:link/app:edited", nsMngr);\n\n    if (node != null)\n    {\n        Console.WriteLine(node.InnerText);\n    }\n    else\n    {\n        Console.WriteLine("Node not found.");\n    }\n}