using System;\nusing System.Text;\n\npublic class EncodingChecker\n{\n    public static bool CanEncodeAllCharacters(string text, Encoding encoding)\n    {\n        // Set the EncoderFallback to throw on any unmappable character\n        var encoder = encoding.WithFallback(new EncoderExceptionFallback());\n        \n        try\n        {\n            // Try to get the byte count for the entire string\n            encoder.GetByteCount(text);\n            return true; // All characters can be encoded\n        }\n        catch (EncoderFallbackException)\n        {\n            return false; // At least one character cannot be encoded\n        }\n    }\n\n    public static void Main()\n    {\n        string text = "Here is some text including 漢字 and other characters.";\n        \n        bool canEncodeInIso88592 = CanEncodeAllCharacters(text, Encoding.GetEncoding("ISO-8859-2"));\n        Console.WriteLine($"Can ISO-8859-2 encode all characters? {canEncodeInIso88592}");\n        \n        bool canEncodeInUnicode = CanEncodeAllCharacters(text, Encoding.UTF8);\n        Console.WriteLine($"Can UTF-8 encode all characters? {canEncodeInUnicode}");\n    }\n}