using System;\nusing System.IO;\n\nclass Program\n{\n    private const string HostsFilePath = @"C:\Windows\System32\drivers\etc\hosts";\n    \n    static void Main(string[] args)\n    {\n        Console.WriteLine("Enter 'block' to block a website or 'unblock' to unblock a website.");\n        string action = Console.ReadLine().ToLower();\n\n        Console.WriteLine("Enter the domain name:");\n        string domain = Console.ReadLine();\n\n        if (action == "block")\n            BlockWebsite(domain);\n        else if (action == "unblock")\n            UnblockWebsite(domain);\n    }\n\n    private static void BlockWebsite(string domain)\n    {\n        try\n        {\n            string lineToWrite = $"127.0.0.1\t{domain}\n";\n            File.AppendAllText(HostsFilePath, lineToWrite);\n\n            Console.WriteLine($"Blocked {domain} by editing hosts file.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n\n    private static void UnblockWebsite(string domain)\n    {\n        try\n        {\n            string[] lines = File.ReadAllLines(HostsFilePath);\n            using (var writer = new StreamWriter(File.Open(HostsFilePath, FileMode.Create)))\n            {\n                foreach (string line in lines)\n                {\n                    if (!line.Contains(domain))\n                        writer.WriteLine(line);\n                }\n            }\n\n            Console.WriteLine($"Unblocked {domain} by editing hosts file.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}