using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic class TimeZoneConverter\n{\n    public static DataTable GetLocalTimeData()\n    {\n        // Create and open connection to SQL Server\n        using (SqlConnection conn = new SqlConnection("your_connection_string"))\n        {\n            conn.Open();\n\n            // Define query to fetch dates in UTC from the database\n            string sqlQuery = "SELECT Id, EventDateTime FROM Events";\n\n            // Execute command\n            using (SqlCommand cmd = new SqlCommand(sqlQuery, conn))\n            using (SqlDataAdapter adapter = new SqlDataAdapter(cmd))\n            {\n                DataTable dataTable = new DataTable();\n\n                // Fill data into the DataTable\n                adapter.Fill(dataTable);\n\n                // Convert UTC dates to local time\n                for (int i = 0; i < dataTable.Rows.Count; i++)\n                {\n                    DateTime utcDateTime = (DateTime)dataTable.Rows[i]["EventDateTime"];\n                    TimeZoneInfo localTimeZoneInfo = TimeZoneInfo.Local;\n                    DateTime localDateTime = TimeZoneInfo.ConvertTimeFromUtc(utcDateTime, localTimeZoneInfo);\n                    dataTable.Rows[i]["EventDateTime"] = localDateTime;\n                }\n\n                return dataTable;\n            }\n        }\n    }\n}