using System;\nusing System.Net.Sockets;\n\npublic class PortChecker\n{\n    public static bool IsPortFree(int port)\n    {\n        try\n        {\n            using (TcpListener listener = new TcpListener(System.Net.IPAddress.Any, port))\n            {\n                listener.Start();\n                listener.Stop();\n                return true;\n            }\n        }\n        catch (SocketException)\n        {\n            // Port is in use or some other socket error\n            return false;\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        int portToCheck = 12345; // Replace with the port you want to check\n        bool isFree = IsPortFree(portToCheck);\n        Console.WriteLine($"Port {portToCheck} is free: {isFree}");\n    }\n}