protected override void OnValidating(CancelEventArgs e)\n{\n    base.OnValidating(e);\n\n    // Regular expression pattern for MM/DD/YYYY format\n    string pattern = @"^(0[1-9]|1[0-2])/(0[1-9]|[12][0-9]|3[01])/(\d{4})$";\n    \n    if (!Regex.IsMatch(Text, pattern))\n    {\n        // Set the error message for the TextBox\n        this.ErrorProvider.SetError(this, "Please enter date in MM/DD/YYYY format.");\n        \n        // Cancel the validation to prevent focus from moving elsewhere\n        e.Cancel = true;\n    }\n    else\n    {\n        // Clear any previous error message if the input is valid\n        this.ErrorProvider.SetError(this, string.Empty);\n    }\n}