using System;\nusing System.DirectoryServices;\n\nclass Program\n{\n    static void Main()\n    {\n        // Connect to IIS metabase\n        DirectoryEntry server = new DirectoryEntry("IIS://LocalHost/W3SVC");\n        foreach (DirectoryEntry site in server.Children)\n        {\n            Console.WriteLine($"Site ID: {site.Properties["ServerBindings"].Value}");\n            \n            if (site.Properties.Contains("SecureBindings"))\n            {\n                foreach (object binding in site.Properties["SecureBindings"])\n                {\n                    Console.WriteLine($"SSL Binding: {binding}");\n                }\n            }\n        }\n    }\n}