using Xunit;\nusing Moq;\n\npublic class QuestionsControllerTests\n{\n    [Fact]\n    public void Should_Return_Questions_When_GetQuestions_Is_Called()\n    {\n        // Arrange\n        var mockRepository = new Mock<IQuestionsRepository>();\n        mockRepository.Setup(repo => repo.GetQuestions()).Returns(new List<Question> { new Question() });\n        \n        var controller = new QuestionsController(mockRepository.Object);\n\n        // Act\n        var result = controller.GetQuestions();\n\n        // Assert\n        var okResult = result as OkObjectResult;\n        Assert.NotNull(okResult);\n        var questionsList = okResult.Value as List<Question>;\n        Assert.NotNull(questionsList);\n        Assert.Single(questionsList);\n    }\n}