using System;\nusing System.Security.Cryptography;\nusing Microsoft.SharePoint;\n\npublic class SecureListItem\n{\n    private static byte[] EncryptStringToBytes_Aes(string plainText, byte[] Key, byte[] IV)\n    {\n        // Use AesManaged to create a local object with default key and IV.\n        using (Aes aesAlg = Aes.Create())\n        {\n            aesAlg.Key = Key;\n            aesAlg.IV = IV;\n\n            // Create an encryptor to perform the stream transform.\n            ICryptoTransform encryptor = aesAlg.CreateEncryptor(aesAlg.Key, aesAlg.IV);\n\n            // Create the streams used for encryption.\n            using (MemoryStream msEncrypt = new MemoryStream())\n            {\n                using (CryptoStream csEncrypt = new CryptoStream(msEncrypt, encryptor, CryptoStreamMode.Write))\n                {\n                    using (System.IO.StreamWriter swEncrypt = new System.IO.StreamWriter(csEncrypt))\n                    {\n                        //Write all data to the stream.\n                        swEncrypt.Write(plainText);\n                    }\n                    return msEncrypt.ToArray();\n                }\n            }\n        }\n    }\n\n    public void EncryptListItem(SPListItem item, string columnName, string valueToEncrypt, byte[] Key, byte[] IV)\n    {\n        byte[] encryptedBytes = EncryptStringToBytes_Aes(valueToEncrypt, Key, IV);\n        item[columnName] = Convert.ToBase64String(encryptedBytes);\n        item.Update();\n    }\n}