public class CustomTabControl {\n    public CustomTabControl Dock(DockStyle dock) { this.Dock = dock; return this; }\n    public CustomTabControl Alignment(TabAlignment alignment) { this.Alignment = alignment; return this; }\n    public CustomTabControl TabCount(int count) { this.TabCount = count; return this; }\n\n    // Properties\n    public DockStyle Dock { get; set; }\n    public TabAlignment Alignment { get; set; }\n    public int TabCount { get; set; }\n}\n\n// Usage\nCustomTabControl tabControl = new CustomTabControl()\n    .Dock(DockStyle.Fill)\n    .Alignment(TabAlignment.Top)\n    .TabCount(3);