using System.ComponentModel.DataAnnotations;\nusing System;\n\npublic class LoginViewModel\n{\n    [Required(ErrorMessage = "Email Address is required.")]\n    [EmailAddress(ErrorMessage = "Please enter a valid email address.")]\n    [ValidDomain]\n    public string EmailAddress { get; set; }\n}\n\npublic class ValidDomainAttribute : ValidationAttribute\n{\n    protected override ValidationResult IsValid(object value, ValidationContext validationContext)\n    {\n        if (value != null && value is string emailAddress)\n        {\n            string[] allowDomains = {"example.com", "anotherdomain.com"};  // List of allowed domains\n            var domain = new Uri(emailAddress).Host;\n            if (!allowDomains.Contains(domain))\n            {\n                return new ValidationResult($"The email must belong to the following domains: {string.Join(", ", allowDomains)}");\n            }\n        }\n\n        return ValidationResult.Success;\n    }\n}