public class ImageUploadService : IImageUploadService\n{\n    public void UploadImage(Stream imageData)\n    {\n        // Here you would process the stream, e.g., save the image to disk or database\n        string path = @"C:\Images\uploaded.jpg";\n        \n        using (FileStream fileStream = new FileStream(path, FileMode.Create))\n        {\n            imageData.CopyTo(fileStream);\n        }\n    }\n}