using System;\nusing System.Data;\nusing System.Data.OleDb;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = @"C:\path\to\yourfile.csv";\n        DataTable dataTable = ReadCsvToDataTable(filePath);\n\n        // Rename columns if necessary\n        foreach (DataColumn column in dataTable.Columns)\n        {\n            column.ColumnName = column.ColumnName.Replace('#', '.');\n        }\n\n        // Now you can use the dataTable with correct column names\n    }\n\n    static DataTable ReadCsvToDataTable(string filePath)\n    {\n        string connectionString = $"Provider=Microsoft.ACE.OLEDB.12.0;Data Source={System.IO.Path.GetDirectoryName(filePath)};Extended Properties=\"Text;HDR=YES;FMT=Delimited\"";\n        using (OleDbConnection connection = new OleDbConnection(connectionString))\n        {\n            string query = $"SELECT * FROM [{System.IO.Path.GetFileName(filePath)}]";\n            connection.Open();\n            using (OleDbDataAdapter adapter = new OleDbDataAdapter(query, connection))\n            {\n                DataTable dataTable = new DataTable();\n                adapter.Fill(dataTable);\n                return dataTable;\n            }\n        }\n    }\n}