using System.Security.Cryptography;\nusing System.Security.Cryptography.X509Certificates;\n\nSHA1Managed sha1 = new SHA1Managed();\nX509Certificate2 certificate = certificatEnCours; // Ensure this is your X.509 certificate\n\n// Compute hash of the entire certificate's raw data\nbyte[] computedHash = sha1.ComputeHash(certificate.RawData);\n\n// Convert the thumbprint from hexadecimal to bytes for comparison\nbyte[] storedThumbprintBytes = StringToByteArray(certificate.Thumbprint);\n\n// Compare the computed hash with the expected thumbprint\nbool verif = computedHash.SequenceEqual(storedThumbprintBytes);\n\nstatic byte[] StringToByteArray(string hex) {\n    int NumberChars = hex.Length;\n    byte[] bytes = new byte[NumberChars / 2];\n    for (int i = 0; i < NumberChars; i += 2)\n        bytes[i / 2] = Convert.ToByte(hex.Substring(i, 2), 16);\n    return bytes;\n}