using System;\nusing System.Security.Cryptography;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new instance of RSACryptoServiceProvider.\n        using (RSACryptoServiceProvider rsa = new RSACryptoServiceProvider())\n        {\n            // Export the full XML representation of the RSA key pair.\n            string publicKeyXml = rsa.ToXmlString(false); // false means public only\n            Console.WriteLine("Public Key:");\n            Console.WriteLine(publicKeyXml);\n            \n            // Note: Private key would be obtained with true in ToXmlString(true)\n        }\n    }\n}