public partial class YourForm : Form\n{\n    public YourForm()\n    {\n        InitializeComponent();\n        // Attach event handlers\n        yourTextBox.TextChanged += new EventHandler(yourTextBox_TextChanged);\n        this.Load += new EventHandler(YourForm_Load);\n    }\n\n    private void yourTextBox_TextChanged(object sender, EventArgs e)\n    {\n        ValidateInput(yourTextBox.Text);\n    }\n\n    private void YourForm_Load(object sender, EventArgs e)\n    {\n        // Perform initial validation when the form loads\n        ValidateInput(yourTextBox.Text);\n    }\n\n    private void ValidateInput(string input)\n    {\n        if (string.IsNullOrEmpty(input))\n        {\n            errorProvider1.SetError(yourTextBox, "This field cannot be empty.");\n        }\n        else\n        {\n            // Add more specific validation rules as needed\n            try\n            {\n                int number = int.Parse(input); // Example: validate integer input\n                errorProvider1.Clear();\n            }\n            catch (FormatException)\n            {\n                errorProvider1.SetError(yourTextBox, "Please enter a valid integer.");\n            }\n        }\n    }\n}