protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack) // Check if it's the first page load\n    {\n        ViewState["ShowModal"] = true;\n    }\n\n    if (Convert.ToBoolean(ViewState["ShowModal"]))\n    {\n        ClientScript.RegisterStartupScript(Page.GetType(), "ModalPopup", \n            "Sys.Application.add_load(function() {ShowModalPopup('" + Me.formModalButton.ID + "')});", true);\n        ViewState["ShowModal"] = false; // Reset after showing\n    }\n}