public partial class frmPin : Form\n{\n    static string connString = @"Provider=Microsoft.ACE.OLEDB.12.0;Data Source=C:\Users\Public\MyDatabase.accdb;Persist Security Info=True";\n    static private int pin = 11; //The First Pin in my records, for debugging I inserted it directly.\n    static string selectStatement = "SELECT Amount FROM Accounts WHERE(PIN=" + pin + ")";\n    OleDbConnection conn;\n    OleDbCommand cmd;\n\n    public frmPin()\n    {\n        InitializeComponent();\n        conn = new OleDbConnection(connString);\n        cmd = new OleDbCommand(selectStatement, conn); // Ensure the command uses the correct connection\n    }\n\n    private void btnQry_Click(object sender, EventArgs e)\n    {\n        try\n        {\n            conn.Open();\n            OleDbDataReader reader = cmd.ExecuteReader(); // executes query\n            while (reader.Read()) // if can read row from database\n            {\n                txtBx.Text = reader.GetValue(0).ToString(); // Corrected to index 0 as you are selecting one column\n            }\n        }\n        catch (Exception ex)\n        {\n            txtBx.Text = "Exception: " + ex.Message;  // Displays only the exception message\n        }\n        finally\n        {\n            conn.Close();  // finally closes connection\n        }\n    }   \n}