using System;\nusing System.IO;\nusing iText.Kernel.Pdf;\nusing iText.Kernel.Pdf.Canvas.Parser;\n\npublic class PdfSearcher\n{\n    public static bool SearchPdfForString(string pdfPath, string searchString)\n    {\n        using (PdfReader reader = new PdfReader(pdfPath))\n        using (PdfDocument document = new PdfDocument(reader))\n        {\n            for (int page = 1; page <= document.GetNumberOfPages(); page++)\n            {\n                string textFromPage = PdfTextExtractor.GetTextFromPage(document.GetPage(page));\n                if (textFromPage.Contains(searchString, StringComparison.OrdinalIgnoreCase))\n                {\n                    return true;\n                }\n            }\n        }\n        return false;\n    }\n\n    public static void Main()\n    {\n        bool found = SearchPdfForString("example.pdf", "your search string");\n        Console.WriteLine("Search string found: " + found);\n    }\n}