public class Program\n{\n    public static void Main()\n    {\n        using (var context = new MyDbContext())\n        {\n            var itemService = new ItemService(context);\n            \n            try\n            {\n                List<Item> itemsToDisplay = itemService.GetTwoRandomItems(userId: 1);\n                \n                // Display the two random items to the user\n                Console.WriteLine($"Random Items: {itemsToDisplay[0].Id}, {itemsToDisplay[1].Id}");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine(ex.Message);\n            }\n        }\n    }\n}