<%@ Page Language="C#" %>\n   <%\n       string path = Request.Path.Substring(1);\n       if (!path.Contains(".")) {\n           // Check if it's a directory with default.aspx\n           if (Directory.Exists(Server.MapPath(path))) {\n               Response.Redirect(path + "/default.aspx");\n           } else {\n               // Redirect to specific file by adding .aspx extension\n               Response.Redirect(path + ".aspx");\n           }\n       }\n   %>