using System;\nusing System.Runtime.InteropServices;\n\npublic class WindowHandleExample\n{\n    // Import necessary user32.dll function\n    [DllImport("user32.dll", SetLastError = true)]\n    static extern IntPtr FindWindow(string lpClassName, string lpWindowName);\n\n    // Example method to get a window text from hwnd\n    public static string GetWindowText(IntPtr hWnd)\n    {\n        int length = GetWindowTextLength(hWnd);\n        if (length == 0) return null;\n\n        StringBuilder sb = new StringBuilder(length + 1);\n        GetWindowText(hWnd, sb, sb.Capacity);\n        return sb.ToString();\n    }\n\n    // Import necessary user32.dll function to get window text\n    [DllImport("user32.dll", CharSet = CharSet.Auto)]\n    static extern int GetWindowText(IntPtr hWnd, StringBuilder lpString, int nMaxCount);\n\n    // Import necessary user32.dll function to get window text length\n    [DllImport("user32.dll")]\n    static extern int GetWindowTextLength(IntPtr hWnd);\n\n    public static void Main()\n    {\n        IntPtr hwnd = FindWindow(null, "Untitled - Notepad"); // Example: Find a notepad window\n        if (hwnd == IntPtr.Zero)\n        {\n            Console.WriteLine("Failed to find the window.");\n            return;\n        }\n\n        string caption = GetWindowText(hwnd);\n        Console.WriteLine($"Window Caption: {caption}");\n    }\n}