using System.Collections.ObjectModel;\nusing System.ComponentModel;\n\npublic class MyViewModel : INotifyPropertyChanged\n{\n    public ObservableCollection<string> ListBoxItems { get; set; }\n    \n    private Visibility _contentControlVisibility;\n    public Visibility ContentControlVisibility\n    {\n        get => _contentControlVisibility;\n        set \n        { \n            _contentControlVisibility = value; \n            OnPropertyChanged(nameof(ContentControlVisibility));\n        }\n    }\n\n    public MyViewModel()\n    {\n        ListBoxItems = new ObservableCollection<string>();\n        ListBoxItems.CollectionChanged += (s, e) =>\n        {\n            ContentControlVisibility = ListBoxItems.Count > 0 ? Visibility.Visible : Visibility.Collapsed;\n        };\n    }\n\n    public event PropertyChangedEventHandler PropertyChanged;\n\n    protected virtual void OnPropertyChanged(string propertyName)\n    {\n        PropertyChanged?.Invoke(this, new PropertyChangedEventArgs(propertyName));\n    }\n}