using System;\nusing DinkToPdf;\nusing DinkToPdf.Contracts;\n\npublic void ConvertHtmlToPdf(string htmlContent, string outputFilePath)\n{\n    var converter = new SynchronizedConverter(new PdfTools());\n    \n    var doc = new HtmlToPdfDocument()\n    {\n        GlobalSettings = {\n            ColorMode = ColorMode.Color,\n            Orientation = Orientation.Portrait,\n            PaperSize = PaperKind.A4Plus\n        },\n        Objects = {\n            new ObjectSettings() {\n                PagesCount = true,\n                HtmlContent = htmlContent,\n                WebSettings = { DefaultEncoding = "utf-8" }\n            }\n        }\n    };\n    \n    converter.Convert(doc, outputFilePath);\n}\n\n// Usage example:\nstring htmlWithImages = "<html><head></head><body><h1>Hello World</h1><img src=\"file:///path/to/image.jpg\" /></body></html>";\nConvertHtmlToPdf(htmlWithImages, "output.pdf");