using System;\nusing System.Diagnostics;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    // Importing necessary Windows API functions.\n    [DllImport("user32.dll", SetLastError = true)]\n    private static extern bool SetWindowText(IntPtr hWnd, string lpString);\n\n    [DllImport("user32.dll", SetLastError = true)]\n    private static extern IntPtr FindWindow(string lpClassName, string lpWindowName);\n\n    static void Main()\n    {\n        // Start Notepad\n        Process notepadProcess = Process.Start("notepad.exe");\n        \n        // Wait for a short time to ensure Notepad window is created.\n        System.Threading.Thread.Sleep(1000);\n\n        // Get the handle of the Notepad main window\n        IntPtr hWnd = FindWindow(null, "Untitled - Notepad");\n\n        if (hWnd != IntPtr.Zero)\n        {\n            // Set new window title\n            bool result = SetWindowText(hWnd, "New Notepad Name");\n            \n            if (!result)\n            {\n                Console.WriteLine("Failed to set new window text.");\n            }\n        }\n        else\n        {\n            Console.WriteLine("Notepad window not found.");\n        }\n    }\n}