/// <summary>Return the square root of x.</summary>\ndouble Sqrt(double x)\n{\n    bool GoodEnough(double guess) => Math.Abs(Square(guess) - x) < 0.001;\n\n    double Improve(double guess) => Average(guess, x / guess);\n\n    double SqrtIter(double guess) => GoodEnough(guess) ? guess : SqrtIter(Improve(guess));\n\n    return SqrtIter(1.0);\n}\n\ndouble Square(double n) => n * n;\ndouble Average(double a, double b) => (a + b) / 2;