public class MyService : IService\n{\n    public Message GetDataAsJson(string id)\n    {\n        var responseData = new ResponseData { Id = id, Data = "Sample JSON data" };\n        WebOperationContext.Current.OutgoingResponse.ContentType = "application/json";\n        return WebMessage.CreateMessage(WebContentFormat.Json, responseData);\n    }\n\n    public Stream GetDataAsXml(string id)\n    {\n        var responseData = new ResponseData { Id = id, Data = "Sample XML data" };\n        MemoryStream ms = new MemoryStream();\n        XmlSerializer serializer = new XmlSerializer(typeof(ResponseData));\n        serializer.Serialize(ms, responseData);\n        ms.Position = 0;\n        return ms;\n    }\n\n    public ResponseData GetDataViaSoap(string id)\n    {\n        return new ResponseData { Id = id, Data = "Sample SOAP data" };\n    }\n}