using System;\nusing System.Threading;\n\nclass Program\n{\n    private static readonly object lockObject = new object();\n    private static bool conditionMet = false;\n\n    static void Main()\n    {\n        Thread workerThread = new Thread(WorkerMethod);\n        workerThread.Start();\n\n        // Simulate some work and then signal the condition\n        Thread.Sleep(1000);\n\n        lock (lockObject)\n        {\n            conditionMet = true;\n            Monitor.Pulse(lockObject); // Notify the waiting thread\n        }\n\n        workerThread.Join();\n    }\n\n    static void WorkerMethod()\n    {\n        lock (lockObject)\n        {\n            while (!conditionMet)\n            {\n                Console.WriteLine("Waiting for the condition to be met...");\n                Monitor.Wait(lockObject); // Wait until signaled or interrupted\n            }\n            Console.WriteLine("Condition is now met.");\n        }\n    }\n}