using System;\nusing System.Web.UI.WebControls;\n\npublic class CustomGridView : GridView\n{\n    protected override void RenderContents(System.Web.UI.HtmlTextWriter writer)\n    {\n        // Start the table tag\n        writer.Write("<table");\n        \n        if (!string.IsNullOrEmpty(CssClass))\n            writer.Write(" class=\"" + CssClass + "\"");\n\n        if (!string.IsNullOrEmpty(ID))\n            writer.Write(" id=\"" + ID + "\"");\n\n        writer.WriteLine(">");\n\n        // Render <thead>\n        TableSectionRowCollection headerRows = Rows;\n        if (headerRows.Count > 0)\n        {\n            writer.Write("<thead>");\n            foreach (GridViewRow row in headerRows)\n            {\n                if (row.RowType == DataControlRowType.Header)\n                    row.RenderControl(writer);\n            }\n            writer.Write("</thead>");\n        }\n\n        // Render <tfoot>\n        TableFooterRow footerRow = this.FooterRow;\n        if (footerRow != null)\n        {\n            writer.Write("<tfoot><tr>");\n            foreach (DataControlFieldCell cell in footerRow.Cells)\n            {\n                cell.RenderControl(writer);\n            }\n            writer.Write("</tr></tfoot>");\n        }\n\n        // Render <tbody>\n        writer.Write("<tbody>");\n        foreach (GridViewRow row in Rows)\n        {\n            if (row.RowType == DataControlRowType.DataRow || row.RowType == DataControlRowType.EmptyData)\n                row.RenderControl(writer);\n        }\n        writer.Write("</tbody>");\n\n        // End the table tag\n        writer.WriteLine("</table>");\n    }\n}