using System;\nusing System.Drawing;\nusing System.Runtime.InteropServices;\nusing System.Threading.Tasks;\n\npublic class ScreenRecorder\n{\n    [DllImport("user32.dll")]\n    private static extern IntPtr GetDesktopWindow();\n\n    public async Task RecordScreen(string outputPath, int durationSeconds)\n    {\n        var desktopPtr = GetDesktopWindow();\n        using (var bitmap = new Bitmap(Screen.PrimaryScreen.Bounds.Width, Screen.PrimaryScreen.Bounds.Height))\n        {\n            var graphics = Graphics.FromImage(bitmap);\n            for (int i = 0; i < durationSeconds * 10; i++)\n            {\n                graphics.CopyFromScreen(0, 0, 0, 0, bitmap.Size);\n                // Save each frame here: bitmap.Save(...)\n\n                await Task.Delay(100); // 10 fps\n            }\n        }\n    }\n}