using System;\nusing System.IO;\n\npublic class FileChecker\n{\n    public static bool IsTextFile(string filePath, int readLength = 4096)\n    {\n        if (!File.Exists(filePath)) return false;\n\n        using (FileStream fs = new FileStream(filePath, FileMode.Open, FileAccess.Read))\n        {\n            // Read only a portion of the file for quick checking\n            byte[] buffer = new byte[Math.Min(readLength, fs.Length)];\n            int numReadBytes = fs.Read(buffer, 0, buffer.Length);\n\n            if (numReadBytes == 0) return true; // File is empty or cannot be read\n\n            // Check each byte to see if it's a text character\n            for (int i = 0; i < numReadBytes; i++)\n            {\n                // Assuming control characters are non-printable, except tab, cr, and lf\n                if (buffer[i] <= 31 && buffer[i] != 9 /*tab*/ && buffer[i] != 10 /*lf*/ && buffer[i] != 13) /*cr*/\n                {\n                    return false; // Found a control character that's not cr, lf, or tab\n                }\n            }\n\n            return true;\n        }\n    }\n}