protected void Application_BeginRequest(object sender, EventArgs e)\n{\n    // Get the current HTTP context\n    HttpContext context = HttpContext.Current;\n\n    // Log the necessary details\n    string ipAddress = context.Request.UserHostAddress;\n    string userAgent = context.Request.UserAgent;\n    DateTime timestamp = DateTime.Now;\n    string httpMethod = context.Request.HttpMethod;\n    string requestData = "";\n    \n    if (httpMethod == "POST")\n        requestData = new StreamReader(context.Request.InputStream).ReadToEnd();\n\n    // Log this information to your custom SQL Server database or any logging service\n    LogRequest(ipAddress, userAgent, timestamp, httpMethod, requestData);\n}\n\nprivate void LogRequest(string ipAddress, string userAgent, DateTime timestamp, string httpMethod, string requestData)\n{\n    using (SqlConnection connection = new SqlConnection("YourConnectionStringHere"))\n    {\n        SqlCommand command = new SqlCommand($"INSERT INTO Requests (IPAddress, UserAgent, Timestamp, HttpMethod, RequestData) VALUES (@ipAddress, @userAgent, @timestamp, @httpMethod, @requestData)", connection);\n        command.Parameters.AddWithValue("@ipAddress", ipAddress);\n        command.Parameters.AddWithValue("@userAgent", userAgent);\n        command.Parameters.AddWithValue("@timestamp", timestamp);\n        command.Parameters.AddWithValue("@httpMethod", httpMethod);\n        command.Parameters.AddWithValue("@requestData", requestData);\n\n        connection.Open();\n        command.ExecuteNonQuery();\n    }\n}