// Example of using iTextSharp for PDF export\nusing System.IO;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\npublic void ExportToPdf(string htmlContent, string filePath)\n{\n    Document document = new Document();\n    PdfWriter.GetInstance(document, new FileStream(filePath, FileMode.Create));\n    document.Open();\n\n    // Parsing HTML content to PDF is more complex and might need a third-party parser\n    // This example assumes text content only for simplicity\n    Paragraph paragraph = new Paragraph(htmlContent);\n    document.Add(paragraph);\n\n    document.Close();\n}\n\n// Example of using Novacode.DocX for Word export\nusing Novacode;\n\npublic void ExportToWord(string htmlContent, string filePath)\n{\n    DocX doc = DocX.Create(filePath);\n    \n    // Convert HTML to text/plain for simplicity; advanced conversion libraries like HtmlRenderer might be needed\n    doc.InsertParagraph(htmlContent);\n    \n    doc.Save();\n}